--
-- Name: sales_payments_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION sales_payments_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	TR RECORD;
  	VDL BOOLEAN;
  	PAYMENTMETHOD RECORD;
  	VAR_COMISION NUMERIC;
  	VAR_RET_IVA NUMERIC;
  	VAR_RET_RENTA NUMERIC;
  	CFEE payment_method_fees%ROWTYPE;
  	VAR_DECIMALS INTEGER;
  	VAR_CURRENCY_ID BIGINT;
  	VAR_PM_DECIMALS INTEGER;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;

  	IF ( TR.payment_method_id IS NOT NULL ) THEN
  		TR.credit = FALSE;
  	END IF;

  	-- SE DETERMINA SI EL PAGO ES DIFERIDO -------------------------
  	IF (TR.credit = FALSE AND TG_OP <> 'DELETE' AND TR.payment_method_id IS NOT NULL) THEN
  		SELECT delayed INTO TR.delayed FROM payment_methods WHERE TR.payment_method_id = payment_methods.id;
  	END IF;
  	----------------------------------------------------------------

  	SELECT currencies.decimals, currencies.id INTO VAR_DECIMALS, VAR_CURRENCY_ID FROM currencies, sales WHERE sales.id = TR.sale_id AND sales.currency_id = currencies.id;

  	IF ( TR.currency_id IS NULL OR TR.currency_id = VAR_CURRENCY_ID ) THEN
  		VAR_PM_DECIMALS := VAR_DECIMALS;
  	ELSE
  		SELECT currencies.decimals INTO VAR_PM_DECIMALS FROM currencies WHERE currencies.id = TR.currency_id;
  	END IF;

  	-- SE DETERMINA EL MONTO EN LA MONEDA DE LA VENTA SI ES QUE
  	-- EL PAGO ESTA EN UNA MONEDA DISTINTA
  	IF (TR.currency_amount IS NOT NULL AND TR.currency_amount <> 0) THEN
  		RAISE NOTICE 'SE DEFINE EL MONTO EN LA MONEDA DE LA TRANSACCION';
  		TR.amount := TR.currency_amount / TR.currency_price;
  	END IF;
	
  	SELECT * INTO PAYMENTMETHOD from payment_methods where payment_methods.id = TR.payment_method_id;
	
  	-- cambio de forma de pago
  	IF (TG_OP='UPDATE') THEN
  		IF NEW.payment_method_id is not null THEN 
  			IF (coalesce(PAYMENTMETHOD.id,-1) <> coalesce(OLD.payment_method_id,-1)) THEN 
  				delete from sales_pnotes where sales_payment_method_id=TR.id;
  				TR.credit:=PAYMENTMETHOD.credit;
  			END IF;
  		ELSE
  			IF (TR.sale_credit_note_id IS NULL AND TR.advance = FALSE) THEN
  				TR.credit:=TRUE;
  			END IF;
  		END IF;
  	END IF;

  	IF(TG_OP='INSERT' OR TG_OP='UPDATE') THEN
  		TR.amount = ROUND(TR.amount, VAR_PM_DECIMALS);
  	END IF;

  	IF (TR.advance = TRUE) THEN
  		TR.credit = FALSE;
  	END IF;
	
  	-- DETERMINACION DE LA COMISION DE LA PROCESADORA Y RETENCIONES DE IMPUESTOS
  	VAR_COMISION := 0;
  	VAR_RET_IVA := 0;
  	VAR_RET_RENTA := 0;

  	VAR_COMISION := TR.amount * PAYMENTMETHOD.fee / 100;
  	VAR_RET_IVA := TR.amount * PAYMENTMETHOD.ret_iva / 100;
  	VAR_RET_RENTA := TR.amount * PAYMENTMETHOD.ret_rent / 100;

  	IF ( PAYMENTMETHOD.custom_fee ) THEN
  		SELECT
  			*
  		INTO
  			CFEE
  		FROM
  			payment_method_fees
  		WHERE
  			payment_method_id = PAYMENTMETHOD.id
  		AND
  			min_amount <= TR.amount
  		ORDER BY
  			min_amount DESC
  		LIMIT
  			1
  		;
  		IF (CFEE.id IS NOT NULL) THEN
  			IF (CFEE."type" = 'percent') THEN
  				VAR_COMISION := TR.amount * CFEE.amount / 100;
  			ELSE
  				VAR_COMISION := CFEE.amount;
  			END IF;
  		END IF;
  	END IF;
	
  	TR.commission := ROUND(VAR_COMISION, VAR_DECIMALS);
  	TR.withholding_vat := ROUND(VAR_RET_IVA, VAR_DECIMALS);
  	TR.withholding_income := ROUND(VAR_RET_RENTA, VAR_DECIMALS);

  	RETURN TR;
  END
  $$;


