-- Name: sales_points_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION sales_points_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
	DECLARE	
		TR RECORD;
		TR2 RECORD;
	BEGIN
		IF (TG_OP='DELETE') THEN
			TR := OLD;
			TR2 := OLD;
		ELSE
			TR := NEW;
			IF ( TG_OP = 'UPDATE' ) THEN
				TR2 := OLD;
			ELSE
				TR2 := NEW;
			END IF;
		END IF;

		IF ( TR.points > 0 OR TR2.points > 0 ) THEN
			PERFORM update_contact_points(TR.contact_id);
			IF ( TR.contact_id <> TR2.contact_id ) THEN
				PERFORM update_contact_points(TR2.contact_id);
			END IF;
		END IF;

		RETURN TR;
	END
	$$;


