--

CREATE FUNCTION sales_product_production_items_update_stock() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	SALE sales%ROWTYPE;
	SALES_PRODUCT sales_products%ROWTYPE;
	PRODUCT products%ROWTYPE;
	POSALE posales%ROWTYPE;
	PTYPE ptypes%ROWTYPE;
	STOCK_STATUS CHARACTER VARYING;
	STOCK_RECEIVED TIMESTAMP WITHOUT TIME ZONE;
	STOCK_TOTAL_RECEIVED NUMERIC;
	PRODQ NUMERIC;
	V_PRODUCED NUMERIC;
BEGIN
	RAISE NOTICE 'sales_product_production_items_update_stock';
	IF (TG_OP='DELETE') THEN
		TR := OLD;
		DELETE FROM stocks WHERE sales_product_production_item_id = TR.id;
	ELSE
		IF (TG_OP='UPDATE') THEN
			IF (NEW.quantity = OLD.quantity) THEN
				RETURN NEW;
			END IF;
		END IF;
		
		TR := NEW;
		SELECT * INTO SALES_PRODUCT FROM sales_products WHERE id = TR.sales_product_id;
		
		SELECT * INTO SALE FROM sales WHERE id = SALES_PRODUCT.sale_id;

		SELECT * INTO PRODUCT FROM products WHERE id = TR.product_id;

		IF ( PRODUCT.stockable = FALSE AND PRODUCT.composers = 0 ) THEN
			RETURN NEW;
		END IF;

		IF (SALE.leasing) THEN
			DELETE FROM stocks WHERE sales_product_id = TR.id;
			DELETE FROM leasings WHERE sales_product_id = TR.id;
			INSERT INTO leasings (
				sales_product_id,
				contact_id,
				product_id,
				quantity,
				start_date,
				end_date,
				company_id
			)
			VALUES (
				TR.id,
				SALE.contact_id,
				TR.product_id,
				TR.quantity * SALES_PRODUCT.aum_factor,
				COALESCE(SALE.leasing_start, DATE(NOW())),
				COALESCE(SALE.leasing_end, DATE(NOW())),
				SALE.company_id
			);
			RETURN TR;
		END IF;

		IF (SALE.stock = FALSE) THEN
			DELETE FROM stocks WHERE sales_product_id = SALES_PRODUCT.id;
			DELETE FROM stocks WHERE sales_product_production_item_id = TR.id;
			RETURN TR;
		END IF;
		
		SELECT * INTO PTYPE FROM ptypes WHERE id = PRODUCT.ptype_id;
		SELECT * INTO POSALE FROM posales where id = SALE.posale_id;
		
		IF ((PTYPE.produced = TRUE OR PRODUCT.composers > 0) AND POSALE.production_allowed) THEN
			RAISE NOTICE 'ES UN PRODUCTO COMPUESTO';
			-- SE ESTABLECE LA CANTIDAD A PRODUCIR
			-- Si hay disponibilidad se produce solo el saldo ( PENDIENTE )
			-- Se produce la cantidad minima o la cantidad indicada ( PENDIENTE )
			PRODQ := TR.quantity;
			-- IF (PTYPE.produced = FALSE OR (POSALE.production_allowed = FALSE AND SALE.preorder = FALSE)) THEN
			IF (PTYPE.produced = FALSE AND SALE.preorder = FALSE) THEN
				RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE NO REQUIERE PRODUCCION';
				-- SIMPLEMENTE SE DESCUENTAN LOS COMPONENTES DE STOCK
				DELETE FROM stocks WHERE sales_product_production_item_id = TR.id;
				INSERT INTO stocks (
					product_id,
					total_sent,
					price,
					currency_id,
					warehouse_id,
					total,
					received,
					status,
					sales_product_id,
					sales_product_production_item_id,
					total_tourism_scheme
				)
				SELECT
					product_components.component_product_id,
					product_components.quantity * -1 * PRODQ * SALES_PRODUCT.aum_factor,
					products.buy_price,
					products.buy_price_currency_id,
					( COALESCE(
						( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
						( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
						( SELECT warehouse_id FROM ptypes WHERE ptypes.id = PRODUCT.ptype_id ),
						-- ( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
						( PTYPE.warehouse_id ),
						( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id ),
						( SELECT MIN(id) FROM warehouses WHERE company_id = SALE.company_id AND agency_id = SALE.agency_id ),
						( SELECT MIN(id) FROM warehouses ),
						NULL
					) ),
					quantity * -1 * PRODQ * SALES_PRODUCT.aum_factor,
					NOW(),
					'delivered',
					SALES_PRODUCT.id,
					TR.id,
					0
				FROM
					product_components,
					products
				WHERE
					product_components.component_product_id = products.id
				AND
					product_components.product_id = PRODUCT.id
				;
			ELSE
				IF(TG_OP='UPDATE') THEN
					IF(NEW.quantity > OLD.quantity) THEN
						-- SE GENERA UNA ORDEN DE PRODUCCION
						RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE REQUIERE PRODUCCION';

						IF ( SALE.porders_ready ) THEN
							V_PRODUCED := (NEW.quantity-OLD.quantity) * SALES_PROUCT.aum_factor;
						ELSE
							V_PRODUCED := 0;
						END IF;
						
						--DELETE FROM porders WHERE sales_product_id = TR.id;
						INSERT INTO porders (
							cep_id,
							user_id,
							priority,
							delivery_datetime,
							sales_product_id,
							sales_product_production_item_id
						)
						VALUES (
							COALESCE( ( SELECT ceps_ptypes.cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps_ptypes.ptype_id = PRODUCT.ptype_id AND ceps.agency_id=SALE.agency_id LIMIT 1 ),( SELECT cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps.agency_id=SALE.agency_id LIMIT 1 ), ( SELECT MIN(id) FROM ceps WHERE company_id = SALE.company_id ) ),
							SALE.user_id,
							3,
							COALESCE(SALE.delivery_datetime, NOW()),
							SALES_PRODUCT.id,
							TR.id
						);
						INSERT INTO porders_products (
							porder_id,
							product_id,
							requested,
							produced
						)
						VALUES (
							( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
							PRODUCT.id,
							(NEW.quantity-OLD.quantity) * SALES_PRODUCT.aum_factor,
							V_PRODUCED
						);
					END IF;
					IF (NEW.quantity < OLD.quantity) THEN
						IF ( SALE.porders_ready ) THEN
							V_PRODUCED := (NEW.quantity-OLD.quantity) * SALES_PRODUCT.aum_factor;
						ELSE
							V_PRODUCED := 0;
						END IF;
						DELETE FROM porders WHERE sales_product_production_item_id = TR.id;
						INSERT INTO porders (
							cep_id,
							user_id,
							priority,
							delivery_datetime,
							sales_product_id,
							sales_product_production_item_id
						)
						VALUES (
							COALESCE( ( SELECT ceps_ptypes.cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps_ptypes.ptype_id = PRODUCT.ptype_id AND ceps.agency_id=SALE.agency_id LIMIT 1 ),( SELECT cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps.agency_id=SALE.agency_id LIMIT 1 ), ( SELECT MIN(id) FROM ceps WHERE company_id = SALE.company_id ) ),
							SALE.user_id,
							3,
							COALESCE(SALE.delivery_datetime, NOW()),
							SALES_PRODUCT.id,
							TR.id
						);
						INSERT INTO porders_products (
							porder_id,
							product_id,
							requested,
							produced
						)
						VALUES (
							( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
							PRODUCT.id,
							(PRODQ) * TR.aum_factor,
							V_PRODUCED
						);
					END IF;
				ELSE
					-- SE GENERA UNA ORDEN DE PRODUCCION
					RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE REQUIERE PRODUCCION';
					DELETE FROM porders WHERE sales_product_production_item_id = TR.id;
					INSERT INTO porders (
						cep_id,
						user_id,
						priority,
						delivery_datetime,
						sales_product_id,
						sales_product_production_item_id
					)
					VALUES (
						COALESCE( ( SELECT ceps_ptypes.cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps_ptypes.ptype_id = PRODUCT.ptype_id AND ceps.agency_id=SALE.agency_id LIMIT 1 ),( SELECT cep_id FROM ceps_ptypes inner join ceps ON ceps_ptypes.cep_id=ceps.id WHERE ceps.agency_id=SALE.agency_id LIMIT 1 ), ( SELECT MIN(id) FROM ceps WHERE company_id = SALE.company_id ) ),
						SALE.user_id,
						3,
						COALESCE(SALE.delivery_datetime, NOW()),
						SALES_PRODUCT.id,
						TR.id
					);
					INSERT INTO porders_products (
						porder_id,
						product_id,
						requested
					)
					VALUES (
						( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
						PRODUCT.id,
						PRODQ * SALES_PRODUCT.aum_factor
					);

				END IF;
			END IF;
		ELSE
			RAISE NOTICE 'EL PRODUCTO NO ES COMPUESTO O NO REQUIERE PRODUCCION';
			-- IF ((PTYPE.produced = TRUE OR PRODUCT.composers > 0) AND POSALE.production_allowed) THEN
			RAISE NOTICE 'PTYPE.produced=%',PTYPE.produced;
			RAISE NOTICE 'PRODUCT.composers=%',PRODUCT.composers;
			RAISE NOTICE 'PRODUCT.id=%',PRODUCT.id;
			DELETE FROM stocks WHERE sales_product_id = TR.id;
			INSERT INTO stocks (
				product_id,
				total_sent,
				price,
				currency_id,
				warehouse_id,
				total,
				received,
				status,
				sales_product_id,
				sales_product_production_item_id,
				total_tourism_scheme
			)
			VALUES (
				PRODUCT.id,
				ABS(TR.quantity) * -1 * SALES_PRODUCT.aum_factor,
				PRODUCT.buy_price,
				PRODUCT.buy_price_currency_id,
				( COALESCE(
					( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
					( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
					( PTYPE.warehouse_id ),
					( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id ),
					( SELECT MIN(id) FROM warehouses WHERE company_id = SALE.company_id AND agency_id = SALE.agency_id ),
					( SELECT MIN(id) FROM warehouses ),
					NULL
				) ),
				ABS(TR.quantity) * -1 * SALES_PRODUCT.aum_factor,
				NOW(),
				'delivered',
				SALES_PRODUCT.id,
				TR.id,
				ABS(SALES_PRODUCT.total_tourism_scheme) * -1
			);
		END IF;
	END IF;
		
	RETURN TR;
END
$$;


