CREATE FUNCTION sales_products_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
    TR RECORD;
    PTYPE ptypes%ROWTYPE;
    product_unique boolean;
    _SALE RECORD;
    _PRODUCT RECORD;
    _APPOINTMENT RECORD;
    _DAYOFF RECORD;
    _C RECORD;
    _A BOOLEAN;
    _A2 BOOLEAN;
    _A3 INTEGER;
  
  BEGIN
    IF (TG_OP='DELETE') THEN
      TR := OLD;
    ELSE
      TR := NEW;
    END IF;

    -- CALCULO DEL TOTAL DE LA VENTA
    PERFORM set_sale_total(TR.sale_id); 
    IF (TG_OP = 'UPDATE') THEN
  	IF (NEW.sale_id <> OLD.sale_id) THEN
  		PERFORM set_sale_total(OLD.sale_id); 
  	END IF;
    END IF;

    -- SE DETERMINA SI LA VENTA REQUIERE APROBACION
    -- O REVISION DE DOCUMENTOS SEGUN
    -- LA CATEGORIA DE LOS PRODUCTOS
    IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN
      SELECT * INTO PTYPE FROM ptypes WHERE ptypes.id = ( SELECT ptype_id FROM products WHERE products.id = TR.product_id );
      UPDATE
        sales
      SET
        requires_approval = PTYPE.requires_approval,
        requires_documents = PTYPE.requires_documents
        -- approved = NOT PTYPE.requires_approval,
        -- documents = NOT PTYPE.requires_documents
      WHERE
        id = NEW.sale_id
      ;
      -- select products.unique into product_unique from products where products.unique = true and id = TR.product_id;
      --
      -- IF (product_unique =true)THEN
      --
      -- update products set sellable = false where id = TR.product_id;
      --
      -- END IF;

    END IF;

    -- SI LA VENTA QUEDO VACIA Y ESTA EN ESTADO PENDIENTE, SE ELIMINA
    IF (TG_OP = 'UPDATE' OR TG_OP = 'DELETE') THEN
      IF ( ( SELECT COUNT(*) FROM sales_products WHERE sale_id = OLD.sale_id ) = 0 ) THEN
        DELETE FROM sales WHERE id = OLD.sale_id AND status = 'pending';
      END IF;
    END IF;

    IF ( TR.repnote_id IS NOT NULL ) THEN
      PERFORM set_repnote_total_paid( TR.repnote_id );
    END IF;

    IF(TG_OP<>'DELETE') THEN
      SELECT * INTO _SALE FROM sales WHERE id = TR.sale_id;
      SELECT * INTO _APPOINTMENT FROM appointments WHERE id = TR.appointment_id;
      SELECT * INTO _PRODUCT FROM products WHERE id = TR.product_id;
      IF _SALE.billed = true AND _SALE.void = FALSE AND TR.appointment_id IS NOT NULL AND TR.professional_id IS NOT NULL THEN
        SELECT * INTO _C FROM professionals_commissions WHERE sales_product_id = TR.id;
        IF _C.approved IS NOT NULL THEN
          _A := _C.approved;
          _A2 := _C.approved_admin;
          _A3 := _C.approved_admin_id;
        ELSE
        END IF;
        DELETE FROM professionals_commissions WHERE sales_product_id = TR.id;

          INSERT INTO professionals_commissions 
            (contact_id,
            rate,
            price,
            paid,
            last_pay_date,
            appointment_id,
            sale_id,
            created,
            product,
            client_id,
            product_id,
            issue_date,
            sales_product_id,
            method,
            approved,
            approved_admin,
            approved_admin_id)
          VALUES
            (TR.professional_id,
            "getCommissionPerc"(TR.appointment_product_id::integer),
            TR.price * TR.quantity,
            0,
            NULL,
            TR.appointment_id,
            TR.sale_id,
            NOW(),
            TRUE,
            _SALE.contact_id,
            TR.product_id,
            _APPOINTMENT.issue_date,
            TR.id,
            "getCommissionMethod"(TR.appointment_product_id::integer),
            _A,
            _A2,
            _A3
          );
      END IF;
    END IF;

    IF (TR.gloans_pnote_id IS NOT NULL) THEN
  	PERFORM set_gloans_pnote_totals(TR.gloans_pnote_id);
    END IF;
  
    RETURN TR;
  END
  $$;


