	  				NEW.price := round(NEW.price,CURRENCY.decimals);
	  			END IF;
	  			-- RAISE NOTICE 'NEW.price=%', NEW.price;
	  		ELSE
	  			RAISE NOTICE '--- 2 ---';
	  			-- NEW.notes := NEW.notes || '--- 1 --- \n';
  			
				RAISE NOTICE 'PRODUCT.price_open=%', PRODUCT.price_open;
				RAISE NOTICE 'SALE.edited_prices=%', SALE.edited_prices;
				RAISE NOTICE 'PMOD=%', PMOD;
	  			-- NEW.notes := NEW.notes || ', ELSE DEL IF 1';
			
	  			IF((SALE.notaxes = TRUE OR NEW.notaxes) AND NEW.price < PRODUCT.sell_price AND SALE.edited_prices = FALSE) THEN
	  				IF (SALE.leasing = FALSE) THEN
	  					IF ( NEW.quantity <> 0.5 OR PRODUCT.half_portion_price IS NULL ) THEN
	  						-- RAISE NOTICE 'PRECIO NORMAL';
	  						DC_PRICE := PRODUCT.sell_price;
	  					ELSE
	  						-- RAISE NOTICE 'PRECIO MITAD';
	  						DC_PRICE := PRODUCT.half_portion_price * 2;
	  					END IF;
	  				ELSE
	  					DC_PRICE := PRODUCT.leasing_price;
	  				END IF;

	  				-- DC_PRICE := DC_PRICE * NEW.aum_factor;

	  				IF (SALE.currency_id <> PRODUCT.sell_price_currency_id) THEN
	  					DC_PRICE := DC_PRICE * PRODUCT_CURRENCY.buy_price;
	  					NEW.price := DC_PRICE / SALE.currency_price;
					
	  					NEW.price := NEW.price - NEW.discount_amount / NEW.quantity;
	  					IF (NEW.discount_amount = 0) THEN
	  						NEW.price := (select round(NEW.price,CURRENCY.decimals));
	  					END IF;
	  				END IF;
	  			ELSE
	  				IF (TG_OP = 'UPDATE') THEN
	  					--IF ( OLD.discount_amount <> 0 AND NEW.price_reset = FALSE ) THEN
	  					--	NEW.price := NEW.price + OLD.discount_amount / OLD.quantity;
	  					--END IF;
	  				END IF;
	  			END IF;
			
				IF (NEW.aum_factor > 1) THEN
					NEW.price := ROUND(NEW.price / NEW.aum_factor, CURRENCY.decimals);
				END IF;
			
	  		END IF;
	  		NEW.currency_id := SALE.currency_id;

	  		-- SE DESGRAVA YA AQUI EL IVA
	  		-- PARA EL CALCULO DEL DESCUENTO
	  		IF ((SALE.notaxes = TRUE OR NEW.notaxes) AND PRODUCT.price_open = FALSE AND SALE.edited_prices = FALSE AND NEW.manual_taxed = FALSE) THEN
			
	  			-- NEW.price := DC_PRICE; -- SE REESTABLECE EL VALOR SIN DESCUENTO
			
	  			-- SE LEE EL IVA DEL PRODUCTO
	  			IF (SALE.rental) THEN
	  				-- SE DETERMINA EL IMPUESTO DE ALQUILER
	  				SELECT taxes.* INTO TAX FROM taxes WHERE id = PRODUCT.leasing_tax_id;
	  			ELSE
	  				-- SE DETERMINA EL IMPUESTO DE VENTA
	  				RAISE NOTICE 'se determina el impeusto de la venta';
	  				SELECT taxes.* INTO TAX FROM taxes WHERE id IN ( SELECT tax_id FROM products_taxes WHERE product_id = NEW.product_id LIMIT 1 );
	  			END IF;
	  			IF (TAX.rate IS NOT NULL AND feature('academic') = false) THEN
	  				UNTAXEDVAL := ROUND((NEW.price / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) )), CURRENCY.decimals);
	  				NEW.price := UNTAXEDVAL;
	  			END IF;
			
	  			NEW.price := NEW.price - NEW.discount_amount / NEW.quantity;
	  			IF (NEW.discount_amount = 0) THEN
	  				NEW.price := round(NEW.price,CURRENCY.decimals);
	  			END IF;
			
	  			NEW.tax_id := NULL;
	  			NEW.tax_amount := 0;
	  			NEW.tax_rate := 0;
	  		END IF;

	  		IF SALE.discount_rate > 0 THEN
	  			NEW.discount_rate := SALE.discount_rate;
	  		END IF;

	  			IF (NEW.discount_amount > 0) THEN
	  				IF (NEW.price > 0) THEN
	  					NEW.discount_rate := (NEW.discount_amount/NEW.quantity) * 100 / (NEW.price+NEW.discount_amount);
	  				ELSE
	  					NEW.discount_rate := 100;
	  				END IF;
	  			ELSE
	  				NEW.discount_rate := 0;
	  			END IF;
	  		-- FIN DETERMINACION DE PRECIO

	  		-- DETERMINACION DE PRODUCTOS BAJO EL REGIMEN DE TURISMO
	  		IF (SALE.tourism_scheme AND NEW.tourism_scheme) THEN
	  			SELECT * INTO PTYPE FROM ptypes WHERE id = PRODUCT.ptype_id;
	  			IF (PTYPE.tourism_scheme) THEN
	  				NEW.total_tourism_scheme = NEW.quantity; -- AUXNUM;
	  			END IF;
	  		END IF;
	  		-- FIN DETERMINACION DE PRODUCTOS BAJO EL REGIMEN DE TURISMO

	  		-- DETERMINACION DE IMPUESTOS
	  		--IF ( SALE.billed = TRUE AND SALE.document_type = 'invoice' AND ( SALE.notaxes = FALSE AND NEW.notaxes = FALSE ) ) THEN
	  		IF ( SALE.billed = TRUE AND SALE.document_type = 'invoice' AND SALE.notaxes = FALSE ) THEN
				RAISE NOTICE 'SE DETERMINA EL IVA';
	  			IF (NEW.manual_taxed = TRUE) THEN
	  				SELECT taxes.* INTO TAX FROM taxes WHERE id = NEW.tax_id;
	  			ELSE
	  				SELECT
						taxes.*
					INTO
						TAX
					FROM
						taxes
					WHERE
					-- CONDICIONES DEL PRODUCTO
						id IN (
							SELECT
								tax_id
							FROM
								products_taxes
							WHERE
								product_id = NEW.product_id
							LIMIT
								1
						)
					-- FIN CONDICIONES DEL PRODUCTO
					-- CONDICIONES DE LOCALIDAD DE LA EMPRESA
					AND
						taxes.country_id = COMPANY.country_id
					AND
						(
							(
									taxes.locality_method = 'excluded'
								AND
									( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id AND district_id = COALESCE(COMPANY.district_id, -1) ) = 0
								AND
									( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id AND city_id = COALESCE(COMPANY.city_id, -1) ) = 0

							)
							OR
							(
									taxes.locality_method = 'exclusive'
								AND
									( ( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id ) = 0 OR ( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id AND district_id = COMPANY.district_id ) > 0 )
								AND
									( ( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id ) = 0 OR ( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id AND city_id = COMPANY.city_id ) > 0 )

							)
						)
					-- FIN CONDICIONES DE LOCALIDAD DE LA EMPRESA
					-- CONDICIONES DE LOCALIDAD DEL CLIENTE
					AND
						(
							(
									taxes.locality_method = 'excluded'
								AND
									( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id AND district_id = COALESCE(SALE.contact_district_id, -1) ) = 0
								AND
									( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id AND city_id = COALESCE(SALE.contact_city_id, -1) ) = 0

							)
							OR
							(
									taxes.locality_method = 'exclusive'
								AND
									( ( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id ) = 0 OR ( SELECT COUNT(*) FROM taxes_districts WHERE tax_id = taxes.id AND district_id = SALE.contact_district_id ) > 0 )
								AND
									( ( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id ) = 0 OR ( SELECT COUNT(*) FROM taxes_cities WHERE tax_id = taxes.id AND city_id = SALE.contact_city_id ) > 0 )

							)
						)
					-- FIN CONDICIONES DE LOCALIDAD DEL CLIENTE
					;
	  			END IF;
	  			NEW.tax_id := TAX.id;
	  			NEW.tax_rate := COALESCE(TAX.rate, 0);
	  			ITEM_NOTAXES := FALSE;
	  			IF (NEW.tax_rate > 0) THEN
	  				IF (CURRENCY.default = TRUE) THEN
	  					UNTAXEDVAL := ROUND((NEW.price * (NEW.quantity * NEW.leasing_ticks - NEW.total_tourism_scheme) / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) )), CURRENCY.decimals);
	  				ELSE
	  					UNTAXEDVAL := (NEW.price * (NEW.quantity * NEW.leasing_ticks - NEW.total_tourism_scheme) / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) ));
	  				END IF;
			
	  				NEW.tax_amount := (NEW.price * (NEW.quantity * NEW.leasing_ticks - NEW.total_tourism_scheme)) - UNTAXEDVAL;
	  			ELSE
	  				NEW.tax_amount := 0;
	  				-- SI LA TASA ES 0 PERO EL PRODUCTO TIENE UN IMPUESTO ASOCIADO, ENTONCES SE DESGRAVA EL IVA
	  				IF (PRODUCT.tax_id IS NOT NULL) THEN
						SELECT
							taxes.*
						INTO
							TAX
						FROM
							taxes
						WHERE
							id = PRODUCT.tax_id
						;
						-- I
						-- ITEM_NOTAXES := TRUE;
						NEW.notaxes := TRUE;
					END IF;
	  			END IF;
	  			IF (SALE.notaxes = TRUE OR NEW.notaxes = TRUE) THEN
	  				IF (TAX.rate > 0 AND SALE.edited_prices = FALSE) THEN
	  					NEW.price := NEW.price - NEW.price * (PRODUCT.taxable / 100) * TAX.rate / (100 + TAX.rate);
	  					IF (NEW.discount_amount = 0) THEN
	  						NEW.price := round(NEW.price,CURRENCY.decimals);
	  					END IF;
	  				END IF;
	  				NEW.tax_id := NULL;
	  				NEW.tax_rate := 0;
	  				NEW.tax_amount := 0;
	  			END IF;
				ELSE
					IF (SALE.billed = false) THEN
	  				NEW.tax_id := NULL;
	  				NEW.tax_rate := 0;
	  				NEW.tax_amount := 0;
					END IF;
	  		END IF;
	  		-- FIN DETERMINACION DE IMPUESTOS

	  		IF (SALE.donation = TRUE) THEN
				IF ( SALE.donations_type_id IS NOT NULL ) THEN
					SELECT COALESCE(donations_types.tax_return_rate, 0) INTO NEW.tax_return_rate FROM donations_types WHERE donations_types.id = SALE.donations_type_id;
					NEW.tax_return_amount := ROUND( NEW.tax_amount * NEW.tax_return_rate / 100, CURRENCY.decimals );
					-- NEW.tax_amount :-= NEW.tax_amount - NEW.tax_return_amount;
	 			END IF;
	  		END IF;

	  		-- DETERMINACION DEL COSTO
	  		IF(TG_OP='INSERT' AND SALE.leasing = FALSE) THEN
				IF ( NEW.manual_cost = FALSE ) THEN
		  			NEW.cost := PRODUCT.buy_price + PRODUCT.total_expenses + PRODUCT.total_direct_expenses;
		  			NEW.cost_untaxed := PRODUCT.buy_price_untaxed + PRODUCT.total_expenses + PRODUCT.total_direct_expenses;
		  			IF ( PRODUCT.composers > 0 ) THEN
		  				SELECT get_product_composition_cost( PRODUCT.ID, 1 ) INTO CCOST;
		  				NEW.cost := NEW.cost + CCOST;

		  				SELECT get_product_composition_cost_untaxed( PRODUCT.ID, 1 ) INTO CCOST;
		  				NEW.cost_untaxed := NEW.cost_untaxed + CCOST;
		  			END IF;
				END IF;
	  			NEW.cost_currency_id := PRODUCT.buy_price_currency_id;
	  			SELECT currencies.* INTO PRODUCT_CURRENCY FROM currencies WHERE currencies.id = PRODUCT.buy_price_currency_id;
	  			NEW.cost_currency_buy_price := PRODUCT_CURRENCY.buy_price;
	  			NEW.cost_currency_legal_buy_price := PRODUCT_CURRENCY.legal_buy_price;

	  			NEW.cost_current_currency_price := PRODUCT.buy_price_lr * COALESCE(( SELECT buy_price FROM currencies WHERE id = PRODUCT.buy_price_lr_currency_id ), 1);
	  		END IF;
	  		-- FIN DETERMINACION DEL COSTO

	  		NEW.price_reset := FALSE;

	  		NEW.aum_factor := 1;
	  		NEW.aum_id := NULL;
	  		NEW.product_um_id := NULL;

	  		IF(TG_OP='INSERT' AND NEW.waiter_order_id IS NOT NULL) THEN
				UPDATE waiter_orders SET attended = NOW(), "active" = TRUE WHERE id = NEW.waiter_order_id;
	  		END IF;


	  		RETURN NEW;
	  	END
	  	$$;


