--
-- Name: sales_products_update_stock(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION sales_products_update_stock() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	SALE sales%ROWTYPE;
	PRODUCT products%ROWTYPE;
	POSALE posales%ROWTYPE;
	PTYPE ptypes%ROWTYPE;
	STOCK_STATUS CHARACTER VARYING;
	STOCK_RECEIVED TIMESTAMP WITHOUT TIME ZONE;
	STOCK_TOTAL_RECEIVED NUMERIC;
	PRODQ NUMERIC;
	V_PRODUCED NUMERIC;
	CEP ceps%ROWTYPE;
BEGIN
	RAISE NOTICE 'sales_products_update_stock';
	IF (TG_OP='DELETE') THEN
		TR := OLD;
		DELETE FROM stocks WHERE sales_product_id = TR.id;
	ELSE
		
		IF (NEW.stock = FALSE OR NEW.production_items > 0 OR NEW.delivery_notes_product_id IS NOT NULL) THEN
			IF ( TG_OP = 'UPDATE' ) THEN
				DELETE FROM stocks WHERE sales_product_id = NEW.id;
			END IF;
			RETURN NEW;
		END IF;

		IF (TG_OP='UPDATE') THEN
			RAISE NOTICE 'DEBUG UPDATE';
			IF (NEW.quantity = OLD.quantity AND NEW.stock = OLD.stock AND OLD.total_extras = NEW.total_extras AND OLD.total_exclusions = NEW.total_exclusions) THEN
				RAISE NOTICE 'DEBUG SAME QUANTITY';
				IF (OLD.stock = NEW.stock) THEN
					RAISE NOTICE 'SAME STOCK';
					RETURN NEW;
				END IF;
			END IF;
		END IF;
		
		TR := NEW;
		SELECT * INTO SALE FROM sales WHERE id = NEW.sale_id;

		IF ( SALE.superinvoice ) THEN
			RAISE NOTICE 'DEBUG SUPER INVOICE';
			-- SI ES UNA FACTURACION EN LOTE YA NO SE GENERAN MOVIMIENTOS DE STOCK
			-- PUES LAS VENTAS INTERNAS QUE SE ESTAN FACTURANDO YA LOS GENERARON
			RETURN NEW;
		END IF;

		IF ( SALE.status = 'canceled' OR SALE.void = TRUE OR SALE.rental = TRUE ) THEN
			DELETE FROM stocks WHERE sales_product_id = NEW.id;
			RETURN NEW;
  		END IF;

		IF (TR.stock_product_id IS NULL) THEN
			SELECT * INTO PRODUCT FROM products WHERE id = TR.product_id;
		ELSE
			SELECT * INTO PRODUCT FROM products WHERE id = TR.stock_product_id;
		END IF;

		IF ( PRODUCT.stockable = FALSE AND PRODUCT.composers = 0 ) THEN
			RAISE NOTICE 'DEBUG STOCKABLE';
			RETURN NEW;
		END IF;

		IF (SALE.leasing) THEN
			DELETE FROM stocks WHERE sales_product_id = TR.id;
			DELETE FROM leasings WHERE sales_product_id = TR.id;
			INSERT INTO leasings (
				sales_product_id,
				contact_id,
				product_id,
				quantity,
				start_date,
				end_date,
				company_id
			)
			VALUES (
				TR.id,
				SALE.contact_id,
				TR.product_id,
				TR.quantity * TR.aum_factor,
				COALESCE(SALE.leasing_start, DATE(NOW())),
				COALESCE(SALE.leasing_end, DATE(NOW())),
				SALE.company_id
			);
			RETURN TR;
		END IF;

		IF (SALE.stock = FALSE) THEN
			RAISE NOTICE 'DEBUG SALE NO STOCK';
			DELETE FROM stocks WHERE sales_product_id = TR.id;
			RETURN TR;
		END IF;
		
		SELECT * INTO PTYPE FROM ptypes WHERE id = PRODUCT.ptype_id;
		SELECT * INTO POSALE FROM posales where id = SALE.posale_id;
		
		IF ((PTYPE.produced = TRUE OR PRODUCT.composers > 0) AND POSALE.production_allowed) THEN
			RAISE NOTICE 'ES UN PRODUCTO COMPUESTO';
			-- SE ESTABLECE LA CANTIDAD A PRODUCIR
			-- Si hay disponibilidad se produce solo el saldo ( PENDIENTE )
			-- Se produce la cantidad minima o la cantidad indicada ( PENDIENTE )
			PRODQ := TR.quantity;
			-- IF (PTYPE.produced = FALSE OR (POSALE.production_allowed = FALSE AND SALE.preorder = FALSE)) THEN
			IF (PTYPE.produced = FALSE AND SALE.preorder = FALSE) THEN
				RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE NO REQUIERE PRODUCCION';
				-- SIMPLEMENTE SE DESCUENTAN LOS COMPONENTES DE STOCK
				DELETE FROM stocks WHERE sales_product_id = TR.id;

				PERFORM stock_discount_components(
					TR.id,
					TR.product_id,
					TR.quantity * TR.aum_factor,
					PRODUCT.production_base,
					( COALESCE(
						( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
						( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
						( SELECT warehouse_id FROM ptypes WHERE ptypes.id = PRODUCT.ptype_id ),
						-- ( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
						( PTYPE.warehouse_id ),
						( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id ),
						( SELECT MIN(id) FROM warehouses WHERE company_id = SALE.company_id AND agency_id = SALE.agency_id ),
						( SELECT MIN(id) FROM warehouses ),
						NULL
					) ),
					TR.created,
					0
				);
				
				-- INSERT INTO stocks (
				-- 	product_id,
				-- 	total_sent,
				-- 	price,
				-- 	currency_id,
				-- 	warehouse_id,
				-- 	total,
				-- 	total2,
				-- 	received,
				-- 	status,
				-- 	sales_product_id,
				-- 	total_tourism_scheme,
				-- 	accounting_cost,
				-- 	"method"
				-- )
				-- SELECT
				-- 	product_components.component_product_id,
				-- 	product_components.quantity * -1 * PRODQ * TR.aum_factor,
				-- 	products.buy_price,
				-- 	products.buy_price_currency_id,
				-- 	( COALESCE(
				-- 		( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
				-- 		( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
				-- 		( SELECT warehouse_id FROM ptypes WHERE ptypes.id = PRODUCT.ptype_id ),
				-- 		-- ( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
				-- 		( PTYPE.warehouse_id ),
				-- 		( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id ),
				-- 		( SELECT MIN(id) FROM warehouses WHERE company_id = SALE.company_id AND agency_id = SALE.agency_id ),
				-- 		( SELECT MIN(id) FROM warehouses ),
				-- 		NULL
				-- 	) ),
				-- 	quantity * -1 * PRODQ * TR.aum_factor,
				-- 	quantity * -1,
				-- 	NOW(),
				-- 	'delivered',
				-- 	TR.id,
				-- 	0,
				-- 	CASE WHEN products.composers > 0 THEN
				-- 		get_product_composition_cost_untaxed(products.id, 0)
				-- 	ELSE
				-- 		products.buy_price_untaxed
				-- 	END,
				-- 	'sale'
				-- FROM
				-- 	product_components,
				-- 	products
				-- WHERE
				-- 	product_components.component_product_id = products.id
				-- AND
				-- 	product_components.product_id = PRODUCT.id
				-- ;
			ELSE
				SELECT
					ceps.* INTO CEP
				FROM
					ceps,
					ceps_ptypes
				WHERE
					ceps_ptypes.ptype_id = PRODUCT.ptype_id
				AND
					ceps_ptypes.cep_id = ceps.id
				AND
					ceps.agency_id = SALE.agency_id
				ORDER BY
					ceps.id
				LIMIT
					1
				;

				IF ( CEP.id IS NULL ) THEN
					SELECT
						ceps.* INTO CEP
					FROM
						ceps
					WHERE
						ceps.agency_id = SALE.agency_id
					ORDER BY
						ceps.id
					LIMIT
						1
					;
				END IF;

				IF(TG_OP='UPDATE') THEN
					IF(NEW.quantity > OLD.quantity) THEN
						-- SE GENERA UNA ORDEN DE PRODUCCION
						RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE REQUIERE PRODUCCION';

						IF ( SALE.porders_ready ) THEN
							V_PRODUCED := (NEW.quantity-OLD.quantity) * TR.aum_factor;
						ELSE
							V_PRODUCED := 0;
						END IF;
						
						--DELETE FROM porders WHERE sales_product_id = TR.id;
						INSERT INTO porders (
							company_id,
							cep_id,
							user_id,
							priority,
							delivery_datetime,
							sales_product_id,
							source_warehouse_id,
							destination_warehouse_id
						)
						VALUES (
							SALE.company_id,
							CEP.id,
							SALE.user_id,
							3,
							COALESCE(SALE.delivery_datetime, NOW()),
							TR.id,
							COALESCE(
								( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
								( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
								CEP.warehouse_id
							),
							COALESCE(
								( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
								( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
								CEP.warehouse_id
							)
						);
						INSERT INTO porders_products (
							porder_id,
							product_id,
							requested,
							produced,
							notes
						)
						VALUES (
							( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
							PRODUCT.id,
							(NEW.quantity-OLD.quantity) * TR.aum_factor,
							V_PRODUCED,
							TR.notes
						);
					END IF;
					IF (NEW.quantity < OLD.quantity) THEN
						IF ( SALE.porders_ready ) THEN
							V_PRODUCED := (NEW.quantity-OLD.quantity) * TR.aum_factor;
						ELSE
							V_PRODUCED := 0;
						END IF;
						DELETE FROM porders WHERE sales_product_id = TR.id;
						INSERT INTO porders (
							company_id,
							cep_id,
							user_id,
							priority,
							delivery_datetime,
							sales_product_id,
							source_warehouse_id,
							destination_warehouse_id
						)
						VALUES (
							SALE.company_id,
							CEP.id,
							SALE.user_id,
							3,
							COALESCE(SALE.delivery_datetime, NOW()),
							TR.id,
							COALESCE(
								( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
								( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
								CEP.warehouse_id
							),
							COALESCE(
								( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
								( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
								CEP.warehouse_id
							)
						);
						INSERT INTO porders_products (
							porder_id,
							product_id,
							requested,
							produced,
							notes
						)
						VALUES (
							( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
							PRODUCT.id,
							(PRODQ) * TR.aum_factor,
							V_PRODUCED,
							TR.notes
						);
					END IF;
				ELSE
					-- SE GENERA UNA ORDEN DE PRODUCCION
					RAISE NOTICE 'ES UN PRODUCTO COMPUESTO QUE REQUIERE PRODUCCION';
					DELETE FROM porders WHERE sales_product_id = TR.id;
					INSERT INTO porders (
						company_id,
						cep_id,
						user_id,
						priority,
						delivery_datetime,
						sales_product_id,
						source_warehouse_id,
						destination_warehouse_id
					)
					VALUES (
						SALE.company_id,
						CEP.id,
						SALE.user_id,
						3,
						COALESCE(SALE.delivery_datetime, NOW()),
						TR.id,
						COALESCE(
							( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
							( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
							CEP.warehouse_id
						),
						COALESCE(
							( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
							( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
							CEP.warehouse_id
						)
					);
					INSERT INTO porders_products (
						porder_id,
						product_id,
						requested,
						notes
					)
					VALUES (
						( SELECT MAX(id) FROM porders WHERE user_id = SALE.user_id ),
						PRODUCT.id,
						PRODQ * TR.aum_factor,
						TR.notes
					);

				END IF;
			END IF;
		ELSE
			RAISE NOTICE 'EL PRODUCTO NO ES COMPUESTO O NO REQUIERE PRODUCCION';
			-- IF ((PTYPE.produced = TRUE OR PRODUCT.composers > 0) AND POSALE.production_allowed) THEN
			RAISE NOTICE 'PTYPE.produced=%',PTYPE.produced;
			RAISE NOTICE 'PRODUCT.composers=%',PRODUCT.composers;
			RAISE NOTICE 'PRODUCT.id=%',PRODUCT.id;
			DELETE FROM stocks WHERE sales_product_id = TR.id;
			INSERT INTO stocks (
				product_id,
				total_sent,
				price,
				currency_id,
				warehouse_id,
				total,
				total2,
				received,
				status,
				sales_product_id,
				total_tourism_scheme,
				accounting_cost,
				"method"
			)
			VALUES (
				PRODUCT.id,
				ABS(TR.quantity) * -1 * TR.aum_factor,
				PRODUCT.buy_price,
				PRODUCT.buy_price_currency_id,
				( COALESCE(
					( SELECT warehouse_id FROM posales_warehouses WHERE posale_id = SALE.posale_id AND ptype_id = PRODUCT.ptype_id LIMIT 1 ),
					( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id AND posales.warehouse_exclusive = TRUE ),
					( PTYPE.warehouse_id ),
					( SELECT warehouse_id FROM posales WHERE posales.id = SALE.posale_id ),
					( SELECT MIN(id) FROM warehouses WHERE company_id = SALE.company_id AND agency_id = SALE.agency_id ),
					( SELECT MIN(id) FROM warehouses ),
					NULL
				) ),
				ABS(TR.quantity) * -1 * TR.aum_factor,
				ABS(TR.quantity) * -1,
				SALE.created,
				'delivered',
				TR.id,
				ABS(TR.total_tourism_scheme) * -1,
				CASE WHEN PRODUCT.composers > 0 THEN
					get_product_composition_cost_untaxed(PRODUCT.id, 0)
				ELSE
					PRODUCT.buy_price_untaxed
				END,
				'sale'
			);
		END IF;
	END IF;
		
	RETURN TR;
END
$$;


