CREATE FUNCTION sales_update_production_status(arg_sale bigint) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
	TOTAL_PENDING INTEGER;
	TOTAL_MAKING INTEGER;
	DECLARE REG RECORD;
	VAR_STATUS CHARACTER VARYING;
BEGIN	
	VAR_STATUS := NULL;
	FOR REG IN SELECT porders.* FROM porders, sales_products WHERE sales_products.sale_id = arg_sale AND sales_products.id = porders.sales_product_id  LOOP
		IF ( VAR_STATUS IS NULL OR VAR_STATUS = 'done' OR ( VAR_STATUS = 'done' AND REG.status = 'pending' ) ) THEN
