CREATE FUNCTION set_gloan_totals(arg_gloan_id bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
  DECLARE
    V_TOTAL_PAID_EXPENSES NUMERIC;
    V_TOTAL_PAID_CAPITAL NUMERIC;
    V_TOTAL_PAID_INTERESTS NUMERIC;
    V_TOTAL_PAID_MORATORY NUMERIC;
    V_TOTAL_PAID_SERVICES NUMERIC;
  BEGIN  
 
    SELECT
      COALESCE(SUM(paid_expenses),0),
      COALESCE(SUM(paid_interests),0),
      COALESCE(SUM(paid_moratory),0),
      COALESCE(SUM(paid_capital),0),
      COALESCE(SUM(paid_services),0)
    INTO
      V_TOTAL_PAID_EXPENSES,
      V_TOTAL_PAID_INTERESTS,
      V_TOTAL_PAID_MORATORY,
      V_TOTAL_PAID_CAPITAL,
      V_TOTAL_PAID_SERVICES
    FROM
      gloans_pnotes
    WHERE
      gloan_id = arg_gloan_id
    ;

    UPDATE
      gloans
    SET
      total_paid_capital = V_TOTAL_PAID_CAPITAL,
      total_paid_interests = V_TOTAL_PAID_INTERESTS,
      total_paid_moratory = V_TOTAL_PAID_MORATORY,
      total_paid_expenses = V_TOTAL_PAID_EXPENSES,
      total_paid_services = V_TOTAL_PAID_SERVICES
    WHERE
      gloans.id = arg_gloan_id
    ;

    UPDATE
    gloans_products
  SET
    total_invoices = COALESCE((
      SELECT
        COUNT(*)
      FROM
        sales,
        sales_products,
        gloans_pnotes
      WHERE
        sales.status = 'approved'
      AND
        sales.void = FALSE
      AND
        sales.id = sales_products.sale_id
      AND
        sales_products.gloans_pnote_id = gloans_pnotes.id
      AND
        gloans_pnotes.gloan_id = gloans_products.gloan_id
      AND
        sales_products.product_id = gloans_products.product_id
    ), 0)
  WHERE
    gloans_products.gloan_id = arg_gloan_id
  ;
  
    RETURN 1;
  END
  $$;


