--
-- Name: set_gloans_pnote_totals(bigint); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION set_gloans_pnote_totals(arg_gloans_pnote_id bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	V_TOTAL_PAID_EXPENSES NUMERIC;
	V_TOTAL_PAID_CAPITAL NUMERIC;
	V_TOTAL_PAID_INTERESTS NUMERIC;
	V_TOTAL_PAID_MORATORY NUMERIC;
	V_TOTAL_PAID_SERVICES NUMERIC;
BEGIN  
 
	SELECT
		SUM(sales_products.price * sales_products.quantity)
	INTO
		V_TOTAL_PAID_EXPENSES
	FROM
		sales_products,
		sales
	WHERE
		sales_products.gloans_pnote_id = arg_gloans_pnote_id
	AND
		sales_products.gloans_pnote_concept = 'expenses'
	AND
		sales_products.sale_id = sales.id
	AND
		sales.void = FALSE
	AND
		sales.status = 'approved'
	;

	SELECT
		SUM(sales_products.price * sales_products.quantity)
	INTO
		V_TOTAL_PAID_INTERESTS
	FROM
		sales_products,
		sales
	WHERE
		sales_products.gloans_pnote_id = arg_gloans_pnote_id
	AND
		sales_products.gloans_pnote_concept = 'interests'
	AND
		sales_products.sale_id = sales.id
	AND
		sales.void = FALSE
	AND
		sales.status = 'approved'
	;

	SELECT
		SUM(sales_products.price * sales_products.quantity)
	INTO
		V_TOTAL_PAID_SERVICES
	FROM
		sales_products,
		sales
	WHERE
		sales_products.gloans_pnote_id = arg_gloans_pnote_id
	AND
		sales_products.gloans_pnote_concept = 'services'
	AND
		sales_products.sale_id = sales.id
	AND
		sales.void = FALSE
	AND
		sales.status = 'approved'
	;

	UPDATE
		gloans_pnotes
	SET
		paid_interests = COALESCE(V_TOTAL_PAID_INTERESTS, 0),
		paid_expenses = COALESCE(V_TOTAL_PAID_EXPENSES, 0),
		paid_services = COALESCE(V_TOTAL_PAID_SERVICES, 0)
	WHERE
		gloans_pnotes.id = arg_gloans_pnote_id
	;
	
	RETURN 1;
END
$$;


