CREATE FUNCTION set_loan_paid(arg_purchase bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
  BEGIN	
  	UPDATE loans SET
  		paid = COALESCE(
  			(
  				SELECT
  					SUM(payments_loans.amount * payments.currency_price / loans.currency_price)
  				FROM
  					loans,
  					payments_loans,
  					payments
  				WHERE
  					loans.id = arg_purchase
  				AND
  					payments_loans.loan_id = loans.id
  				AND
  					payments_loans.payment_id = payments.id
  				AND
  					payments.status IN ('approved', 'pending')
  			),
  		0)
  		+ COALESCE(
  			(
  				SELECT
  					SUM(payments_loans_pnotes.amount * payments.currency_price / loans.currency_price)
  				FROM
  					loans,
  					loans_pnotes,
  					payments_loans_pnotes,
  					payments
  				WHERE
  					loans.id = arg_purchase
  				AND
  					loans_pnotes.loan_id = loans.id
  				AND
  					payments_loans_pnotes.loans_pnote_id = loans_pnotes.id
  				AND
  					payments_loans_pnotes.payment_id = payments.id
  				AND
  					payments.status IN ('approved', 'pending')
  			),
  		0)
  		+ COALESCE(
  			(
  				SELECT
  					SUM(loans_deposits.total_paid)
  				FROM
  					loans_deposits
  				WHERE
  					loans_deposits.loan_id  = loans.id
  			),
  		0)
  	WHERE
  		id = arg_purchase
  	;
	
  	RETURN 1;
  END
  $$;


