--

CREATE FUNCTION set_purchase_cn_discount(purchase bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	PPT purchases_payment_terms%ROWTYPE;
	REG RECORD;
	VAR_DISCOUNT NUMERIC;
	VAR_DISCOUNT_RES NUMERIC;
	VAR_DISCOUNT_ONPURCHASE NUMERIC;
	PPT_RES NUMERIC;
	PPT_APP NUMERIC;
BEGIN	

	SELECT
		SUM(COALESCE(discount_applied, 0)) INTO VAR_DISCOUNT
	FROM
		purchase_credit_notes_purchases
	WHERE
		purchase_id = PURCHASE
	;

	RAISE NOTICE 'VAR_DISCOUNT=%', VAR_DISCOUNT;

	UPDATE
		purchases
	SET
		cn_discount = COALESCE(VAR_DISCOUNT, 0)
	WHERE
		purchases.id = PURCHASE
	;

	UPDATE purchases_payment_terms SET cn_discount = 0 WHERE purchase_id = PURCHASE;
	-- RETURN VAR_DISCOUNT;

	VAR_DISCOUNT_RES := VAR_DISCOUNT;

	SELECT
		COALESCE(SUM(discount_applied), 0) INTO VAR_DISCOUNT_ONPURCHASE
	FROM
		purchase_credit_notes_purchases
	WHERE
		purchase_id = PURCHASE
	AND
		purchase_credit_notes_purchases.onpurchase = TRUE
	;

	RAISE NOTICE 'VAR_DISCOUNT_ONPURCHASE=%', VAR_DISCOUNT_ONPURCHASE;

	VAR_DISCOUNT_RES := VAR_DISCOUNT_RES - VAR_DISCOUNT_ONPURCHASE;
	
	FOR REG IN SELECT purchases_payment_terms.* FROM purchases_payment_terms WHERE purchases_payment_terms.purchase_id = PURCHASE LOOP
		IF (VAR_DISCOUNT_RES > 0) THEN
			PPT_RES := REG.amount - REG.paid - REG.cn_discount;
			IF (PPT_RES > 0) THEN
				IF (PPT_RES < VAR_DISCOUNT_RES) THEN
					PPT_APP := PPT_RES;
				ELSE
					PPT_APP := VAR_DISCOUNT_RES;
				END IF;
				-- PPT_APP := @ PPT_RES - VAR_DISCOUNT_RES;
				UPDATE purchases_payment_terms SET cn_discount = COALESCE(PPT_APP, 0) WHERE id = REG.id;
				VAR_DISCOUNT_RES := VAR_DISCOUNT_RES - PPT_APP;
			END IF;
		END IF;
	END LOOP;
	
	RETURN 1;
END
$$;


