CREATE FUNCTION set_purchase_total(arg_purchase bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	V_TOTAL_AMOUNT NUMERIC;
	V_TOTAL_EXPENSES NUMERIC;
	V_TOTAL_PRODUCTS NUMERIC;
	V_TOTAL_EXPENSES_TAXES NUMERIC;
	V_TOTAL_PRODUCTS_TAXES NUMERIC;
	V_TOTAL_PRODUCTS_DISCOUNT NUMERIC;
	V_TOTAL_EXPENSES_DISCOUNT NUMERIC;
BEGIN	
	-- UPDATE purchases SET
	-- 	amount = COALESCE(( SELECT SUM(price * quantity) FROM purchases_products WHERE purchase_id = arg_purchase ), 0) + COALESCE(( SELECT SUM(price) FROM purchases_expenses WHERE purchase_id = arg_purchase ), 0),
	-- 	total_taxes = COALESCE( (SELECT SUM(tax_amount) FROM purchases_products WHERE purchase_id = arg_purchase), 0 ) + COALESCE( (SELECT SUM(tax_amount) FROM purchases_expenses WHERE purchase_id = arg_purchase), 0 ),
	-- 	discount =  COALESCE(( SELECT SUM(discount_amount) FROM purchases_products WHERE purchase_id = arg_purchase), 0) + COALESCE(( SELECT SUM(discount_amount) FROM purchases_expenses WHERE purchase_id = arg_purchase), 0)
	-- WHERE
	-- 	id = arg_purchase
	-- ;

	SELECT
		SUM(purchases_products.price * purchases_products.quantity),
		SUM(purchases_products.tax_amount),
		SUM(discount_amount)
	INTO
		V_TOTAL_PRODUCTS,
		V_TOTAL_PRODUCTS_TAXES,
		V_TOTAL_PRODUCTS_DISCOUNT
	FROM
		purchases_products
	WHERE
		purchase_id = arg_purchase
	;
	
	SELECT
