CREATE FUNCTION set_purchases_terms_paid(arg_id bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
BEGIN	
	UPDATE
		purchases_payment_terms
	SET
		paid = COALESCE(
			(
				SELECT
					SUM(payment_amount * payments.currency_price / currencies.sale_price)
				FROM
					payments_purchases,
					payments,
					purchases,
					currencies
				WHERE
					payments_purchases.purchases_payment_term_id = purchases_payment_terms.id
				AND
					payments_purchases.payment_id = payments.id
				AND
					payments_purchases.purchase_id = purchases.id
				AND
					purchases.currency_id = currencies.id
				AND
					purchases.currency_id <> payments.currency_id
				AND
					payments.status IN ('approved', 'pending')
			),
			0
		) +
		COALESCE(
			(
				SELECT
					SUM(payment_amount)
				FROM
					payments_purchases,
					payments,
					purchases,
					currencies
				WHERE
					payments_purchases.purchases_payment_term_id = purchases_payment_terms.id
				AND
					payments_purchases.payment_id = payments.id
				AND
					payments_purchases.purchase_id = purchases.id
				AND
					purchases.currency_id = currencies.id
				AND
					( purchases.currency_id = payments.currency_id OR (payments.currency_id IS NULL AND payments.fund_account_id IS NOT NULL) )
				AND
					payments.status IN ('approved', 'pending')
			),
			0
		) +
		COALESCE(
			(
				SELECT
					SUM(swaps_purchases.amount * swaps.currency_price / currencies.sale_price)
