				AND
					swaps_purchases.purchase_id = purchases.id
				AND
					purchases.currency_id = currencies.id
			),
			0
		)
		,
		last_pay_date = ( SELECT MAX(DATE(authorized_on)) FROM payments, payments_purchases WHERE purchases_payment_term_id = purchases_payment_terms.id AND payment_id = payments.id )
	WHERE
		purchases_payment_terms.id = arg_id
	;
	RETURN 1;
