CREATE FUNCTION set_sale_total(arg_sale bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$ 
  DECLARE
  	V_TOTAL_AMOUNT NUMERIC;
  	V_TOTAL_COST NUMERIC;
  	V_TOTAL_COST_UNTAXED NUMERIC;
  	V_COST_CURRENT_CURRENCY_PRICE NUMERIC;
  	V_TOTAL_TAX NUMERIC;
  	V_DISCOUNT_AMOUNT NUMERIC;
