  BEGIN	
  	
  	SELECT
  		SUM(ROUND(sales_products.price * sales_products.quantity * sales_products.leasing_ticks, currencies.decimals)),
  		SUM(sales_products.cost * sales_products.quantity * sales_products.cost_currency_buy_price / sales.currency_price),
  		SUM(sales_products.cost_untaxed * sales_products.quantity * sales_products.cost_currency_buy_price / sales.currency_price),
