  		SUM(sales_products.tax_amount),
  		SUM(sales_products.discount_amount),
  		SUM(sales_products.price * sales_products.quantity * products.consumption_calc::integer),
  		SUM(sales_products.tw_amount),
  		SUM(sales_products.stw_amount),
  		SUM(sales_products.tax_return_amount),
  		SUM(sales_products.total_points_used)
  	INTO
  		V_TOTAL_AMOUNT,
  		V_TOTAL_COST,
  		V_TOTAL_COST_UNTAXED,
  		V_COST_CURRENT_CURRENCY_PRICE,
  		V_TOTAL_TAX,
  		V_DISCOUNT_AMOUNT,
  		V_TOTAL_CON,
  		V_TOTAL_TW,
  		V_TOTAL_STW,
  		V_TOTAL_TRA,
  		V_TOTAL_USED_POINTS
  	FROM
		products,
  		sales_products,
  		sales,
  		currencies
  	WHERE
  		sales_products.sale_id = sales.id
  	AND
  		sales.id = arg_sale
  	AND
		sales_products.product_id = products.id
	AND
		sales.currency_id = currencies.id
  	;
	
  	UPDATE
  		sales
  	SET
  		"amount" = COALESCE(V_TOTAL_AMOUNT, 0) + sales.total_interest,
  		"cost" = COALESCE(V_TOTAL_COST, 0),
  		"cost_untaxed" = COALESCE(V_TOTAL_COST_UNTAXED, 0),
  		"cost_current_currency_price" = COALESCE(V_COST_CURRENT_CURRENCY_PRICE, 0),
  		"total_tax_amount" = COALESCE(V_TOTAL_TAX, 0),
  		"discount" = COALESCE(V_DISCOUNT_AMOUNT, 0),
  		"total_consumption" = COALESCE(V_TOTAL_CON, 0),
  		"tw_amount" = COALESCE(V_TOTAL_TW, 0) + COALESCE(V_TOTAL_STW, 0),
  		"total_tax_return" = COALESCE(V_TOTAL_TRA, 0),
  		"total_points_used" = COALESCE(V_TOTAL_USED_POINTS, 0)
  	WHERE
  		id = arg_sale
  	;
	
  	RETURN 1;
  END
  $$;


