--
-- Name: set_sales_pnotes_paid(bigint); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION set_sales_pnotes_paid(arg_id bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
	  DECLARE
	  	SALE_CURRENCY_PRICE NUMERIC;
	  	SALE_CURRENCY_ID BIGINT;
	  BEGIN	
	  	SELECT sales.currency_price, sales.currency_id INTO SALE_CURRENCY_PRICE, SALE_CURRENCY_ID FROM sales, sales_pnotes WHERE sales_pnotes.id = arg_id AND sales_pnotes.sale_id = sales.id;
	  	UPDATE sales_pnotes SET
	  		total_paid = COALESCE(
	  			(
	  				SELECT
	  					SUM((collections_sales_pnotes.paid-collections_sales_pnotes.overdue_interest) * collections.currency_price) / SALE_CURRENCY_PRICE
	  				FROM
	  					collections_sales_pnotes,
	  					collections
	  				WHERE
	  					collections_sales_pnotes.sales_pnote_id = sales_pnotes.id
	  				AND
	  					collections_sales_pnotes.overdue_interest < collections_sales_pnotes.paid
	  				AND
	  					collection_id = collections.id
	  				AND
	  					collections.currency_id <> SALE_CURRENCY_ID
	  			)
	  			,
	  			0 ) +
	  			COALESCE(
	  				(
	  					SELECT
	  						SUM(collections_sales_pnotes.paid - collections_sales_pnotes.overdue_interest)
	  					FROM
	  						collections_sales_pnotes,
	  						collections
	  					WHERE
	  						collections_sales_pnotes.sales_pnote_id = sales_pnotes.id
	  					AND
	  						collections_sales_pnotes.overdue_interest < collections_sales_pnotes.paid
	  					AND
	  						collection_id = collections.id
	  					AND
	  						collections.currency_id = SALE_CURRENCY_ID
	  				)
	  				,
	  				0 ) +

	  			COALESCE(
	  				(
	  					SELECT
	  						SUM(sales_debts.amount)
	  					FROM
	  						sales_debts,
	  						sales
	  					WHERE
	  						sales_debts.sales_pnote_id = sales_pnotes.id
	  					AND
	  						sales_debts.sale_id = sales.id
	  					AND
	  						sales.currency_id = SALE_CURRENCY_ID
	  				)
	  				,
	  				0 ) +
  				
	  				COALESCE( ( SELECT SUM(swaps_sales.amount) FROM swaps_sales, swaps WHERE swaps_sales.sales_pnote_id = sales_pnotes.id AND swaps_sales.swap_id = swaps.id AND swaps.status = 'confirmed' ), 0 ) +
	  				COALESCE( ( SELECT SUM(amount) FROM sale_credit_notes_sales_pnotes WHERE sales_pnote_id = sales_pnotes.id ), 0 ),
	  		last_pay_date = ( SELECT MAX(issue_date) FROM collections, collections_sales_pnotes WHERE collections_sales_pnotes.sales_pnote_id = sales_pnotes.id AND collections_sales_pnotes.collection_id = collections.id ),
	  		overdue_paid = COALESCE( ( SELECT SUM(paid) FROM collections_sales_pnotes WHERE sales_pnote_id = sales_pnotes.id AND overdue_interest > paid ), 0 ) +
	  				COALESCE( ( SELECT SUM(overdue_interest) FROM collections_sales_pnotes WHERE sales_pnote_id = sales_pnotes.id AND overdue_interest < paid ), 0 )
	  	WHERE
	  		id = arg_id
	  	;
	  	RETURN 1;
	  END
	  $$;


