--

CREATE FUNCTION set_service_order_amount(_service_order_id bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	REG RECORD;
	SERVICE_ORDER service_orders%ROWTYPE;
	PRODUCT products%ROWTYPE;
	_AMOUNT_TAXED NUMERIC;
	_AMOUNT_UNTAXED NUMERIC;
BEGIN	
	SELECT * INTO SERVICE_ORDER FROM service_orders WHERE id = _service_order_id;

	_AMOUNT_TAXED := 0;
	_AMOUNT_UNTAXED := 0;

	FOR REG IN (
		SELECT
			products.name,
			service_orders_products.price,
			service_orders_products.quantity,
			COALESCE( ( SELECT taxes.rate FROM taxes, products WHERE products.id = service_orders_products.product_id AND products.tax_id = taxes.id ), 0 ) AS "tax_rate",
			products.taxable
		FROM
			service_orders_products,
			products
		WHERE
			service_orders_products.service_order_id = _service_order_id
		AND
			service_orders_products.product_id = products.id
	) LOOP
		RAISE NOTICE 'Procesando item: %', REG.name;
		IF ( REG.tax_rate = 0 ) THEN
			_AMOUNT_TAXED := _AMOUNT_TAXED + ( REG.price * REG.quantity );
			_AMOUNT_UNTAXED := _AMOUNT_UNTAXED + ( REG.price * REG.quantity );
		ELSE
			IF ( SERVICE_ORDER.vat_included ) THEN
				_AMOUNT_TAXED := _AMOUNT_TAXED + ( REG.price * REG.quantity );
				_AMOUNT_UNTAXED := _AMOUNT_UNTAXED + ROUND((REG.price * REG.quantity / ( 1 + ( REG.taxable / 100 ) * ( REG.tax_rate / 100 ) )), 0);
			ELSE
				_AMOUNT_UNTAXED := _AMOUNT_UNTAXED + ( REG.price * REG.quantity );
				_AMOUNT_TAXED := ( REG.price * REG.quantity ) + ROUND(( REG.price * REG.quantity ) * REG.tax_rate / 100);
			END IF;
		END IF;
	END LOOP;

	UPDATE service_orders SET amount_taxed = _AMOUNT_TAXED, amount_untaxed = _AMOUNT_UNTAXED WHERE id = SERVICE_ORDER.id;
	
	RETURN 1;
END
$$;


