-- Name: stock_discount_components(bigint, bigint, numeric, numeric, bigint, timestamp without time zone); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION stock_discount_components(arg_sales_product_id bigint, arg_product_id bigint, arg_quantity numeric, arg_production_base numeric, arg_warehouse_id bigint, arg_received timestamp without time zone) RETURNS integer
    LANGUAGE plpgsql
    AS $$
	  	DECLARE
	  		PRODUCT RECORD;
	  		V_THIS_QUANTITY NUMERIC;
	  	BEGIN	
	
	  		FOR PRODUCT IN (
	  			SELECT
	  				products.id,
	  				products.composers,
	  				products.stockable,
	  				products.production_base,
	  				products.buy_price_currency_id,
	  				products.buy_price_untaxed,
	  				product_components.quantity
	  			FROM
	  				products,
	  				product_components
	  			WHERE
	  				products.id = product_components.component_product_id
	  			AND
	  				product_components.product_id = arg_product_id
	  		) LOOP
			
	  			V_THIS_QUANTITY := PRODUCT.quantity / arg_production_base * arg_quantity;
			
	  			IF ( PRODUCT.composers > 0 ) THEN
	  				PERFORM stock_discount_components( arg_sales_product_id, PRODUCT.id, V_THIS_QUANTITY, PRODUCT.production_base, arg_warehouse_id, arg_received );
	  			ELSE
	  				INSERT INTO stocks (
	  					product_id,
	  					total_sent,
	  					warehouse_id,
	  					total,
	  					received,
	  					status,
	  					sales_product_id,
	  					total_tourism_scheme,
	  					price,
	  					currency_id
	  				)
	  				VALUES (
	  					PRODUCT.id,
	  					V_THIS_QUANTITY * -1,
	  					arg_warehouse_id,
	  					V_THIS_QUANTITY * -1,
	  					arg_received,
	  					'delivered',
	  					arg_sales_product_id,
	  					0,
	  					PRODUCT.buy_price_untaxed,
	  					PRODUCT.buy_price_currency_id
	  				);
	  			END IF;
			
	  		END LOOP;
	
	  		RETURN 1;
	
	  	END
	  $$;


