--

CREATE FUNCTION stocks_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
DECLARE
	TR RECORD;
	AUX BIGINT;
	VCOMPANY_ID BIGINT;
	VPRODUCT_ID BIGINT;
	vlast_invads_product_id bigint;
	vlast_invad_quantity numeric;
	vlast_invad_timestamp timestamp without time zone;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	IF(TG_OP = 'UPDATE') THEN
		IF(OLD.serial <> NEW.serial) THEN
			DELETE FROM "availabilities" WHERE warehouse_id = TR.warehouse_id AND product_id = TR.product_id AND serial = OLD.serial;
			UPDATE stocks SET id = id WHERE warehouse_id = TR.warehouse_id AND product_id = TR.product_id AND serial = OLD.serial;
		END IF;
	END IF; 

	IF TR.invads_product_id IS NOT NULL THEN

		vlast_invad_quantity := (select reported_quantity from invads_products where id = TR.invads_product_id);
		vlast_invad_timestamp := (select created from invads_products where id = TR.invads_product_id);
	ELSE
		vlast_invad_quantity := 0;
		vlast_invad_timestamp := null;
	
	END IF;
	
	IF(TR.serial IS NULL) THEN
		DELETE FROM "availabilities" WHERE warehouse_id = TR.warehouse_id AND product_id = TR.product_id AND serial IS NULL;
		INSERT INTO availabilities (
			warehouse_id,
			product_id,
			total,
			total2,
			created,
			last_invads_product_id,
			last_invad_timestamp,
			last_invad_quantity
		)
		SELECT
			TR.warehouse_id,
			TR.product_id,
			COALESCE(SUM(total), 0),
			COALESCE(SUM(total2), 0),
			NOW(),
			TR.invads_product_id,
			vlast_invad_timestamp,
			vlast_invad_quantity
			
		FROM
			stocks
		WHERE
			warehouse_id = TR.warehouse_id
		AND
			product_id = TR.product_id
		AND
			"status" IN ('delivered', 'received')
		AND
			serial IS NULL
		;
	ELSE
		DELETE FROM "availabilities" WHERE warehouse_id = TR.warehouse_id AND product_id = TR.product_id AND serial = TR.serial;
		INSERT INTO availabilities (
			warehouse_id,
			product_id,
			total,
			total2,
			created,
			last_invads_product_id,
			last_invad_timestamp,
			last_invad_quantity,
			serial
		)
		SELECT
			TR.warehouse_id,
			TR.product_id,
			COALESCE(SUM(total), 0),
			COALESCE(SUM(total2), 0),
			NOW(),
			TR.invads_product_id,
			vlast_invad_timestamp,
			vlast_invad_quantity,
			TR.serial
		FROM
			stocks
		WHERE
			warehouse_id = TR.warehouse_id
		AND
			product_id = TR.product_id
		AND
			"status" IN ('delivered', 'received')
		AND
			serial = TR.serial
		;
	END IF;


	-- SE ACTUALIZAN LOS TOTALES DE STOCK DE PRODUCTOS BAJO EL REGIMEN DE TURISMO
	IF ( TR.total_tourism_scheme <> 0 ) THEN
		SELECT warehouses.company_id INTO VCOMPANY_ID FROM warehouses WHERE warehouses.id = TR.warehouse_id;
		DELETE FROM tourism_availabilities WHERE company_id = VCOMPANY_ID AND product_id = TR.product_id;
		VPRODUCT_ID := TR.product_id;
		INSERT INTO tourism_availabilities (
			company_id,
			product_id,
			total
		)
		VALUES (
			VCOMPANY_ID,
			TR.product_id,
			COALESCE(( SELECT SUM(stocks.total_tourism_scheme) FROM stocks, warehouses WHERE stocks.product_id = VPRODUCT_ID AND warehouse_id = warehouses.id AND warehouses.company_id = VCOMPANY_ID ), 0)
		);
	END IF;
		
		
	RETURN TR;
END
$$;


