--
-- Name: tr_presales_products_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION tr_presales_products_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$DECLARE
    TAX taxes%ROWTYPE;
    PRODUCT products%ROWTYPE;
    CURRENCY currencies%ROWTYPE;
    PRODUCT_CURRENCY currencies%ROWTYPE;
    SALE sales%ROWTYPE;
    DC_PRICE NUMERIC;
    PTYPE ptypes%ROWTYPE;
    AUXNUM NUMERIC;
    CCOST NUMERIC;
    PMOD BOOLEAN;
    UNTAXEDVAL NUMERIC;
BEGIN   
    SELECT * INTO SALE FROM presales WHERE presales.id = NEW.sale_id;
    SELECT * INTO CURRENCY FROM currencies WHERE id = SALE.currency_id;
    NEW.currency_id = CURRENCY.id;
    NEW.total_tourism_scheme := COALESCE(NEW.total_tourism_scheme,0);
    NEW.tourism_scheme := COALESCE(NEW.tourism_scheme,false);
    SELECT products.* INTO PRODUCT FROM products WHERE products.id = NEW.product_id;
    SELECT * INTO PRODUCT_CURRENCY FROM currencies WHERE id = PRODUCT.sell_price_currency_id;

    -- SE DESGRAVA YA AQUI EL IVA
    -- PARA EL CALCULO DEL DESCUENTO
    IF (SALE.notaxes = TRUE AND PRODUCT.price_open = FALSE AND SALE.edited_prices = FALSE AND NEW.manual_taxed = FALSE) THEN
      -- SE LEE EL IVA DEL PRODUCTO
      IF (SALE.leasing) THEN
        -- SE DETERMINA EL IMPUESTO DE ALQUILER
        SELECT taxes.* INTO TAX FROM taxes WHERE id = PRODUCT.leasing_tax_id;
      ELSE
        -- SE DETERMINA EL IMPUESTO DE VENTA
        RAISE NOTICE 'se determina el impeusto de la venta';
        SELECT taxes.* INTO TAX FROM taxes WHERE id IN ( SELECT tax_id FROM products_taxes WHERE product_id = NEW.product_id LIMIT 1 );
      END IF;
      IF (TAX.rate IS NOT NULL) THEN
        UNTAXEDVAL := ROUND((NEW.price / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) )), CURRENCY.decimals);
        NEW.price := UNTAXEDVAL;
      END IF;
      NEW.tax_id := NULL;
      NEW.tax_amount := 0;
      NEW.tax_rate := 0;
    END IF;

    IF SALE.discount_rate > 0 THEN
      NEW.discount_rate := SALE.discount_rate;
    END IF;

      IF (NEW.discount_amount > 0) THEN
        IF (NEW.price > 0) THEN
          NEW.discount_rate := (NEW.discount_amount/NEW.quantity) * 100 / NEW.price;
        ELSE
          NEW.discount_rate := 100;
        END IF;
      ELSE
        NEW.discount_rate := 0;
      END IF;
    -- FIN DETERMINACION DE PRECIO

    IF ( SALE.billed = TRUE AND SALE.document_type = 'invoice' AND SALE.notaxes = FALSE ) THEN
      IF (NEW.manual_taxed = TRUE) THEN
        SELECT taxes.* INTO TAX FROM taxes WHERE id = NEW.tax_id;
      ELSE
        IF (SALE.leasing) THEN
          -- SE DETERMINA EL IMPUESTO DE ALQUILER
          SELECT taxes.* INTO TAX FROM taxes WHERE id = PRODUCT.leasing_tax_id;
        ELSE
          -- SE DETERMINA EL IMPUESTO DE VENTA
          SELECT taxes.* INTO TAX FROM taxes WHERE id IN ( SELECT tax_id FROM products_taxes WHERE product_id = NEW.product_id LIMIT 1 );
        END IF;
      END IF;
      NEW.tax_id := TAX.id;
      NEW.tax_rate := COALESCE(TAX.rate, 0);
      IF (TAX.rate IS NOT NULL) THEN
        IF (CURRENCY.default = TRUE) THEN
          UNTAXEDVAL := ROUND((NEW.price * (NEW.quantity - NEW.total_tourism_scheme) / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) )), CURRENCY.decimals);
        ELSE
          UNTAXEDVAL := (NEW.price * (NEW.quantity - NEW.total_tourism_scheme) / ( 1 + ( PRODUCT.taxable / 100 ) * ( TAX.rate / 100 ) ));
        END IF;
      
        NEW.tax_amount := (NEW.price * (NEW.quantity - NEW.total_tourism_scheme)) - UNTAXEDVAL;
      ELSE
        NEW.tax_amount := 0;
      END IF;
      IF (SALE.notaxes = TRUE) THEN
        -- NEW.price := NEW.price - NEW.tax_amount;
        IF (TAX.rate > 0 AND SALE.edited_prices = FALSE) THEN
          NEW.price := NEW.price - NEW.price * (PRODUCT.taxable / 100) * TAX.rate / (100 + TAX.rate);
          NEW.price := round(NEW.price,CURRENCY.decimals);
        END IF;
        NEW.tax_id := NULL;
        NEW.tax_rate := 0;
        NEW.tax_amount := 0;
      END IF;
    END IF;
    -- FIN DETERMINACION DE IMPUESTOS

    -- DETERMINACION DEL COSTO
    IF(TG_OP='INSERT' AND SALE.leasing = FALSE) THEN
      NEW.cost := PRODUCT.buy_price + PRODUCT.total_expenses + PRODUCT.total_direct_expenses;
      NEW.cost_untaxed := PRODUCT.buy_price_untaxed + PRODUCT.total_expenses + PRODUCT.total_direct_expenses;
      IF ( PRODUCT.composers > 0 ) THEN
        SELECT get_product_composition_cost( PRODUCT.ID ) INTO CCOST;
        NEW.cost := NEW.cost + CCOST;
        NEW.cost_untaxed := NEW.cost_untaxed + CCOST;
      END IF;
      NEW.cost_currency_id := PRODUCT.buy_price_currency_id;
      SELECT currencies.* INTO PRODUCT_CURRENCY FROM currencies WHERE currencies.id = PRODUCT.buy_price_currency_id;
      NEW.cost_currency_buy_price := PRODUCT_CURRENCY.buy_price;
      NEW.cost_currency_legal_buy_price := PRODUCT_CURRENCY.legal_buy_price;

      NEW.cost_current_currency_price := PRODUCT.buy_price_lr * COALESCE(( SELECT buy_price FROM currencies WHERE id = PRODUCT.buy_price_lr_currency_id ), 1);
    END IF;
    -- FIN DETERMINACION DEL COSTO

    NEW.price_reset := FALSE;


    RETURN NEW; 
END;$$;


