--
-- Name: update_porder_status(bigint); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION update_porder_status(arg_porder bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	ORDER_STATUS VARCHAR;
	SALES_PRODUCT_ID BIGINT;
	PORDER porders%ROWTYPE;
	CEP ceps%ROWTYPE;
	IT BIGINT;
BEGIN	
	IF ( SELECT COUNT(*) FROM porders_products WHERE porder_id = arg_porder AND requested > produced ) > 0 THEN
		IF ( SELECT COUNT(*) FROM porders_products WHERE porder_id = arg_porder AND received_tm IS NOT NULL ) > 0 THEN
			ORDER_STATUS := 'making';
		ELSE
			ORDER_STATUS := 'pending';
		END IF;
	ELSE
		ORDER_STATUS := 'done';
	END IF;
	UPDATE porders SET status = ORDER_STATUS WHERE id = arg_porder;
	
	IF (ORDER_STATUS = 'done') THEN
		-- SE VERIFICA SI SE DEBE TRANSFERIR LOS ITEMS A OTRO DEPOSITO
		SELECT * INTO PORDER FROM porders WHERE id = arg_porder;
		SELECT * INTO CEP FROM ceps WHERE ceps.id = PORDER.cep_id;
		IF (PORDER.destination_warehouse_id IS NOT NULL AND PORDER.destination_warehouse_id <> CEP.warehouse_id) THEN
			INSERT INTO invtransferences (
				src_warehouse_id,
				dst_warehouse_id,
				user_id
			)
			VALUES (
				CEP.warehouse_id,
				PORDER.destination_warehouse_id,
				PORDER.user_id
			);
			SELECT MAX(id) INTO IT FROM invtransferences;
			UPDATE porders SET invtransference_id = IT WHERE id = PORDER.id;
			INSERT INTO invtransferences_products (
				invtransference_id,
				product_id,
				quantity	
			)
			SELECT
				IT,
				product_id,
				produced
			FROM
				porders_products
			WHERE
				porder_id = PORDER.id
			;
		END IF;
	END IF;
	
	RETURN 1;
END
$$;


