CREATE VIEW expenses_views AS
    SELECT p.contact_id, c.name AS contact_name, p.company_id, co.name AS company_name, p.fund_account_id, fa.name AS fund_account_name, p.currency_id, cu.name AS currency_name, p.amount, p.requested_on, 'Payment'::text AS type FROM ((((payments p JOIN contacts c ON ((p.contact_id = c.id))) JOIN companies co ON ((co.id = p.company_id))) JOIN fund_accounts fa ON ((fa.id = p.fund_account_id))) JOIN currencies cu ON ((cu.id = p.currency_id))) WHERE ((p.status)::text = 'approved'::text) UNION SELECT p.contact_id, c.name AS contact_name, p.company_id, co.name AS company_name, p.fund_account_id, fa.name AS fund_account_name, p.currency_id, cu.name AS currency_name, p.amount, p.issue_date AS requested_on, 'Purchase'::text AS type FROM ((((purchases p JOIN contacts c ON ((p.contact_id = c.id))) JOIN companies co ON ((co.id = p.company_id))) JOIN fund_accounts fa ON ((fa.id = p.fund_account_id))) JOIN currencies cu ON ((cu.id = p.currency_id))) WHERE ((((p.payment_terms)::text = 'cash'::text) AND (p.void = false)) AND ((p.status)::text = 'active'::text));


