    (((SELECT purchases.id, purchases.company_id, purchases_fund_accounts.check_number, purchases_fund_accounts.fund_account_id, purchases.issue_date, purchases.issue_date AS payment_date, purchases.amount, purchases.currency_price, purchases.currency_id, 'purchase'::text AS operation_type, purchases.id AS purchase_id, NULL::integer AS payment_id, NULL::integer AS fajournal_id, NULL::integer AS advances_payments_id, purchases.contact_id FROM purchases, purchases_fund_accounts WHERE (((purchases_fund_accounts.check_number IS NOT NULL) AND ((purchases_fund_accounts.check_number)::text <> ''::text)) AND (purchases.id = purchases_fund_accounts.purchase_id)) UNION SELECT payments.id, payments.company_id, payments.check_number, payments.fund_account_id, COALESCE(date(payments.authorized_on), date(payments.requested_on)) AS issue_date, COALESCE(payments.check_date, date(payments.authorized_on), date(payments.requested_on)) AS payment_date, payments.amount, payments.currency_price, payments.currency_id, 'payment'::text AS operation_type, NULL::integer AS purchase_id, payments.id AS payment_id, NULL::integer AS fajournal_id, NULL::integer AS advances_payments_id, payments.contact_id FROM payments WHERE (((payments.check_number IS NOT NULL) AND ((payments.check_number)::text <> ''::text)) AND (payments.fund_account_id IS NOT NULL))) UNION SELECT payments.id, payments.company_id, payments_fund_accounts.check_number, payments_fund_accounts.fund_account_id, COALESCE(date(payments_fund_accounts.issue_date), date(payments.authorized_on), date(payments.requested_on)) AS issue_date, COALESCE(date(payments_fund_accounts.pay_date), date(payments_fund_accounts.issue_date), date(payments.authorized_on), date(payments.requested_on)) AS payment_date, payments_fund_accounts.amount, payments.currency_price, payments.currency_id, 'payment'::text AS operation_type, NULL::integer AS purchase_id, payments.id AS payment_id, NULL::integer AS fajournal_id, NULL::integer AS advances_payments_id, payments.contact_id FROM payments, payments_fund_accounts WHERE (payments.id = payments_fund_accounts.payment_id)) UNION SELECT fajournals.id, fund_accounts.company_id, fund_transfers.check_number, fund_transfers.src_fund_account_id AS fund_account_id, (fund_transfers.issue_date)::date AS issue_date, (fund_transfers.issue_date)::date AS payment_date, fund_transfers.amount, fund_transfers.currency_price, fund_transfers.currency_id, 'fajournal'::text AS operation_type, NULL::integer AS purchase_id, NULL::integer AS payment_id, fajournals.id AS fajournal_id, NULL::integer AS advances_payments_id, NULL::integer AS contact_id FROM fajournals, fund_accounts, fund_transfers WHERE ((((fajournals.fund_account_id = fund_accounts.id) AND (fajournals.in_fund_transfer_id = fund_transfers.id)) AND (fund_transfers.check_number IS NOT NULL)) AND ((fund_transfers.check_number)::text <> ''::text))) UNION SELECT advances_payments.id, advances_payments.company_id, advances_payments.check_number, advances_payments.fund_account_id, advances_payments.issue_date, advances_payments.issue_date AS payment_date, advances_payments.amount, advances_payments.currency_price, advances_payments.currency_id, 'advance_payment'::text AS operation_type, NULL::integer AS purchase_id, NULL::integer AS payment_id, NULL::integer AS fajournal_id, advances_payments.id AS advances_payments_id, advances_payments.contact_id FROM advances_payments WHERE (advances_payments.check_number IS NOT NULL) ORDER BY 5;


