CREATE VIEW sales_accounts_sums AS
    ((SELECT 'sale'::text AS type, sales.issue_date, sales.posale_id, min((sales.invoice_number)::text) AS min_invoice_number, max((sales.invoice_number)::text) AS max_invoice_number, sales_products.tax_id, sales_products.tax_rate, sum(((((sales_products.price * (sales_products.quantity - sales_products.total_tourism_scheme)) - sales_products.tax_amount) * sales.legal_currency_price) / sales.legal_local_currency_price)) AS taxable, sum(((sales_products.tax_amount * sales.legal_currency_price) / sales.legal_local_currency_price)) AS tax_amount, sum((((sales_products.price * (sales_products.quantity - sales_products.total_tourism_scheme)) * sales.legal_currency_price) / sales.legal_local_currency_price)) AS amount, sum((((sales_products.total_tourism_scheme * sales_products.price) * sales.legal_currency_price) / sales.legal_local_currency_price)) AS notax_amount, 0 AS withholding FROM sales, sales_products WHERE (((sales.id = sales_products.sale_id) AND (sales.void = false)) AND (sales.billed = true)) GROUP BY sales.issue_date, sales.posale_id, sales_products.tax_id, sales_products.tax_rate UNION SELECT 'sale_credit_note'::text AS type, sale_credit_notes.issue_date, sale_credit_notes.posale_id, min((sale_credit_notes.invoice_number)::text) AS min_invoice_number, max((sale_credit_notes.invoice_number)::text) AS max_invoice_number, sale_credit_notes_products.tax_id, sale_credit_notes_products.tax_rate, sum(((((sale_credit_notes_products.price * sale_credit_notes_products.quantity) - sale_credit_notes_products.tax_amount) * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS taxable, sum(((sale_credit_notes_products.tax_amount * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS tax_amount, sum((((sale_credit_notes_products.price * sale_credit_notes_products.quantity) * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS amount, 0 AS notax_amount, 0 AS withholding FROM sale_credit_notes, sale_credit_notes_products WHERE (((sale_credit_notes.id = sale_credit_notes_products.sale_credit_note_id) AND (sale_credit_notes.void = false)) AND (sale_credit_notes.billed = true)) GROUP BY sale_credit_notes.issue_date, sale_credit_notes.posale_id, sale_credit_notes_products.tax_id, sale_credit_notes_products.tax_rate) UNION SELECT 'sale_credit_note'::text AS type, sale_credit_notes.issue_date, sale_credit_notes.posale_id, min((sale_credit_notes.invoice_number)::text) AS min_invoice_number, max((sale_credit_notes.invoice_number)::text) AS max_invoice_number, sale_credit_notes_expenses.tax_id, sale_credit_notes_expenses.tax_rate, sum((((sale_credit_notes_expenses.price - sale_credit_notes_expenses.tax_amount) * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS taxable, sum(((sale_credit_notes_expenses.tax_amount * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS tax_amount, sum(((sale_credit_notes_expenses.price * sale_credit_notes.legal_currency_price) / sale_credit_notes.legal_local_currency_price)) AS amount, 0 AS notax_amount, 0 AS withholding FROM sale_credit_notes, sale_credit_notes_expenses WHERE (((sale_credit_notes.id = sale_credit_notes_expenses.sale_credit_note_id) AND (sale_credit_notes.void = false)) AND (sale_credit_notes.billed = true)) GROUP BY sale_credit_notes.issue_date, sale_credit_notes.posale_id, sale_credit_notes_expenses.tax_id, sale_credit_notes_expenses.tax_rate) UNION SELECT 'ppset'::text AS type, ppsets.issue_date, NULL::bigint AS posale_id, min((ppsets.invoice_number)::text) AS min_invoice_number, max((ppsets.invoice_number)::text) AS max_invoice_number, NULL::bigint AS tax_id, 0 AS tax_rate, 0 AS taxable, 0 AS tax_amount, 0 AS amount, 0 AS notax_amount, sum(ppsets.vat_withholding) AS withholding FROM ppsets GROUP BY ppsets.issue_date ORDER BY 2;


