-- Name: _RETURN; Type: RULE; Schema: public; Owner: dev
--

CREATE RULE "_RETURN" AS ON SELECT TO projects_costs DO INSTEAD ((SELECT invtransferences.id AS type_id, invtransferences.user_id, invtransferences.total_cost AS cost, 'invtransferences'::text AS type, invtransferences.send_datetime AS issue_date, projects.id AS project_id FROM invtransferences_products, invtransferences, projects_warehouses, projects WHERE (((invtransferences.id = invtransferences_products.invtransference_id) AND (invtransferences.dst_warehouse_id = projects_warehouses.warehouse_id)) AND (projects_warehouses.project_id = projects.id)) GROUP BY invtransferences.id, projects.id UNION SELECT invtransferences.id AS type_id, invtransferences.user_id, (invtransferences.total_cost * ((-1))::numeric) AS cost, 'returns'::text AS type, invtransferences.send_datetime AS issue_date, projects.id AS project_id FROM invtransferences_products, invtransferences, projects_warehouses, projects WHERE (((invtransferences.id = invtransferences_products.invtransference_id) AND (invtransferences.src_warehouse_id = projects_warehouses.warehouse_id)) AND (projects_warehouses.project_id = projects.id)) GROUP BY invtransferences.id, projects.id) UNION SELECT precence_records.id AS type_id, precence_records.user_id, precence_records.cost, 'precence_records'::text AS type, precence_records.created AS issue_date, precence_records.project_id FROM precence_records WHERE (precence_records.cost > (0)::numeric)) UNION SELECT projects_fuels_orders.id AS type_id, projects_fuels_orders.user_id, projects_fuels_orders.cost, 'projects_fuels_orders'::text AS type, projects_fuels_orders.created AS issue_date, projects_fuels_orders.project_id FROM projects_fuels_orders WHERE (projects_fuels_orders.cost > (0)::numeric);


